import React from "react";
import { io, Socket } from "socket.io-client";
import { useEffect } from "react";
import {
  ServerToClientEvents,
  ClientToServerEvents
} from "../../../types/socket";

// const socket: Socket<ServerToClientEvents, ClientToServerEvents> = io();
const socket = io("http://localhost:3001/task3/subpage");
const userEntered = () => {
  socket.emit("user_entered", { message: "user logged" });
};
const userLeft = () => {
  socket.emit("user_left", { message: "user left" });
};
const Task3Subpage = () => {
  socket.on("counter", function (msg) {
    console.log("count", msg);
  });

  useEffect(() => {
    const timer = setTimeout(() => {
      userEntered();
    }, 1000);

    window.addEventListener("beforeunload", userLeft);

    return () => {
      window.removeEventListener("beforeunload", userLeft);
      //clearTimeout(timer);
    };
  }, []);
  return <div>index</div>;
};

export default Task3Subpage;
