      query: {
        userId
      }
    });

    //  window.addEventListener("beforeunload", userLeft);

    return () => {
      // window.removeEventListener("beforeunload", userLeft);
      // clearTimeout(timer);
    };
  }, [userId]);
  return <div>index</div>;
};

export default Task3Subpage;
